# Get active connector
connector = env['sicantik.connector'].search([('active', '=', True)], limit=1)
if connector:
    result = connector.sync_permit_types()
    action = {
        'type': 'ir.actions.client',
        'tag': 'display_notification',
        'params': {
            'title': 'Sinkronisasi Jenis Izin Selesai',
            'message': f'Baru: {result["synced"]}, Diperbarui: {result["updated"]}, Gagal: {result["failed"]}',
            'type': 'success' if result['synced'] > 0 or result['updated'] > 0 else 'warning',
            'sticky': False,
        }
    }
else:
    action = {
        'type': 'ir.actions.client',
        'tag': 'display_notification',
        'params': {
            'title': 'Error',
            'message': 'Tidak ada connector aktif',
            'type': 'danger',
            'sticky': False,
        }
    }

