#!/bin/bash

# Script to restart Odoo Standalone with proper configuration
# This script ensures proper memory settings and database access

set -e

# Colors for output
RED='\033[0;31m'
GREEN='\033[0;32m'
YELLOW='\033[1;33m'
BLUE='\033[0;34m'
NC='\033[0m' # No Color

# Function to print colored output
print_status() {
    echo -e "${BLUE}[INFO]${NC} $1"
}

print_success() {
    echo -e "${GREEN}[SUCCESS]${NC} $1"
}

print_warning() {
    echo -e "${YELLOW}[WARNING]${NC} $1"
}

print_error() {
    echo -e "${RED}[ERROR]${NC} $1"
}

# Check if Docker is running
check_docker() {
    if ! docker info > /dev/null 2>&1; then
        print_error "Docker is not running. Please start Docker first."
        exit 1
    fi
    print_success "Docker is running"
}

# Restart Odoo standalone with proper configuration
restart_odoo() {
    print_status "Restarting Odoo Standalone with proper configuration..."

    # Stop the container
    docker-compose stop odoo_companion_standalone

    # Wait a moment
    sleep 3

    # Remove the container to ensure clean restart
    docker-compose rm -f odoo_companion_standalone

    # Start with updated configuration
    docker-compose up -d odoo_companion_standalone

    print_success "Odoo Standalone restarted successfully"
}

# Wait for Odoo to be ready
wait_for_odoo() {
    print_status "Waiting for Odoo to be ready..."

    # Wait for container to start
    sleep 10

    # Check if Odoo is responding
    local count=0
    local max_attempts=30

    while [ $count -lt $max_attempts ]; do
        if curl -s http://localhost:8065 > /dev/null; then
            print_success "Odoo is ready and accessible"
            return 0
        fi

        echo -n "."
        sleep 2
        count=$((count + 1))
    done

    echo ""
    print_warning "Odoo might not be fully ready yet, but the container is started"
}

